import React, { useState } from "react";
import "./App.css"; // Import file CSS untuk styling

function App() {
  const [headingText, setHeading] = useState("");
  const [isMousedOver, setMouseOver] = useState(false);
  const [name, setName] = useState("");
  const [isEditing, setEditing] = useState(false); // State untuk menentukan apakah sedang dalam mode edit

  function handleMouseOver() {
    setMouseOver(true);
  }

  function handleMouseOut() {
    setMouseOver(false);
  }

  function handleChange(event) {
    setName(event.target.value);
  }

  function handleClick(event) {
    setHeading(name);
    event.preventDefault();
  }

  function handleHeadingClick() {
    setHeading("You clicked the heading!");
  }

  function handleDelete() {
    // Reset the heading and input field
    setHeading("");
    setName("");
  }

  function handleEdit() {
    // Toggle mode edit
    setEditing(!isEditing);
  }

  return (
    <div className="container">
      <h1 onClick={handleHeadingClick}>Selamat Datang {headingText}</h1>
      <form onSubmit={handleClick} className="form">
        <input 
          onChange={handleChange}
          type="text" 
          placeholder="What's your name?"
          value={name}
          disabled={!isEditing} // Mengatur input field menjadi disabled jika tidak dalam mode edit
          className="input"
        />
        <div className="button-container">
          <button 
            type="submit"
            style={{backgroundColor: isMousedOver ? "black" : "white"}}
            onMouseOver={handleMouseOver}
            onMouseOut={handleMouseOut}
            disabled={!isEditing} // Mengatur tombol submit menjadi disabled jika tidak dalam mode edit
            className="submit-button"
          >
            Submit
          </button>
          <button onClick={handleEdit} className="edit-button">{isEditing ? "Cancel Edit" : "Edit"}</button> {/* Tombol untuk mengaktifkan/menonaktifkan mode edit */}
        </div>
      </form>
      {/* Button to delete heading and input value */}
      <button onClick={handleDelete} className="delete-button">Delete</button>
    </div>
  );
}

export default App;
