import React, { useState } from "react";

function App() {
  const [headingText, setHeading] = useState("");
  const [isMousedOver, setMouseOver] = useState(false);
  const [name, setName] = useState("");

  function handleMouseOver() {
    setMouseOver(true);
  }

  function handleMouseOut() {
    setMouseOver(false);
  }

  function handleChange(event) {
    setName(event.target.value);
  }

  function handleClick(event) {
    setHeading(name);
    event.preventDefault();
  }

  function handleHeadingClick() {
    setHeading("You clicked the heading!");
  }

  function handleDelete() {
    // Reset the heading and input field
    setHeading("");
    setName("");
  }

  return (
    <div className="container" style={{ display: "flex", alignItems: "center", justifyContent: "center", flexDirection: "column" }}>
      <h1 onClick={handleHeadingClick} style={{ marginBottom: "20px" }}>Selamat Datang {headingText}</h1>
      <div style={{ display: "flex", alignItems: "center" }}>
        <button onClick={handleDelete} style={{ marginRight: "10px" }}>Delete</button>
        <form onSubmit={handleClick} style={{ display: "flex", alignItems: "center", marginRight: "10px" }}>
          <input 
            onChange={handleChange}
            type="text" 
            placeholder="What's your name?"
            value={name}
          />
          <button 
            type="submit"
            style={{ backgroundColor: isMousedOver ? "black" : "white", marginLeft: "10px" }}
            onMouseOver={handleMouseOver}
            onMouseOut={handleMouseOut}
          >
            Submit
          </button>
        </form>
      </div>
    </div>
  );
}

export default App;
